// Dependencies
import { AudioSchedule } from "../src"
import * as fs from "fs"

// Simple wait function
function wait(ms: number) {
    return new Promise( resolve => setTimeout(resolve, ms) );
}

//
(async() => {
    // Vars
    const file = fs.readFileSync("./test/file.ogg");
    const cookie = "_|WARNING:-DO-NOT-SHARE-THIS.--Sharing-this-will-allow-someone-to-log-in-as-you-and-to-steal-your-ROBUX-and-items.|_";
    const uploader = new AudioSchedule(cookie);

    // Wait
    await wait(1000)

    // Upload
    await uploader.scheduleAudio({
        audio: file,
        name: "test",
        description: "",
        filename: "test",
        filetype: "ogg"
    }, new Date());
})();